import { TouchableOpacity, Image } from "react-native";

const Icon = ({ onPress, png }) => {
  return (
    <TouchableOpacity onPress={onPress}>
      <Image
        source={require(`./img/${png}.png`)}
        // source={require(`.img/${png}.png`)}

        style={{ width: 24, height: 24 }}
      />
    </TouchableOpacity>
  );
};

export default Icon;
