import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { useNavigation } from "@react-navigation/native";
import { Ionicons } from "@expo/vector-icons";
import { getVariable, setVariable, removeVariable } from "./utils/isLogIn";

import Home from "./Screens/Home";
import Login from "./Screens/LoginScreen";
import Registration from "./Screens/RegistrationScreen";
import PostsScreen from "./components/PostsScreen";
import CreatePostsScreen from "./Screens/CreatePostsScreen";
import CommentsScreen from "./Screens/CommentsScreen";
import ProfileScreen from "./Screens/ProfileScreen";

const navigation = useNavigation();
const AuthStack = createStackNavigator();
const AuthNavigator = () => (
  <AuthStack.Navigator
    initialRouteName="Login"
    screenOptions={{ headerShown: false }}
  >
    <AuthStack.Screen name="Registration" component={Registration} />
    <AuthStack.Screen name="Login" component={Login} />
  </AuthStack.Navigator>
);

const Tab = createBottomTabNavigator();
const TabNavigator = () => (
  <Tab.Navigator
    screenOptions={({ route }) => ({
      tabBarIcon: ({ focused, color, size }) => {
        let iconName;

        if (route.name === "Profile") {
          iconName = focused ? "ios-person" : "ios-person";
        } else if (route.name === "Add") {
          iconName = focused ? "ios-add-circle" : "ios-add-circle-outline";
        } else if (route.name === "Posts") {
          iconName = focused ? "ios-grid" : "ios-grid";
        }
        return <Ionicons name={iconName} size={size} color={color} />;
      },
    })}
    tabBarOptions={{
      activeTintColor: "tomato",
      inactiveTintColor: "gray",
    }}
  >
    <Tab.Screen
      name="Posts"
      component={PostsScreen}
      options={{
        title: "Публікації",
        headerStyle: {
          backgroundColor: "#fff",
        },
        headerTintColor: "#212121",
        headerTitleStyle: {
          fontWeight: "500",
          fontSize: 18,
          letterSpacing: -0.4,
        },
        headerRight: () => (
          <Ionicons
            name="ios-log-out"
            size={25}
            color="grey"
            style={{ marginRight: 25 }}
            onPress={() => {
              removeVariable("entrance");
              // setVariable("entrance", false);
            }}
          />
        ),
      }}
    />
    <Tab.Screen
      name="Add"
      component={CreatePostsScreen}
      options={{
        title: "Створити публікацію",
        headerStyle: {
          backgroundColor: "#fff",
        },
        headerTintColor: "#212121",
        headerTitleStyle: {
          fontWeight: "500",
          fontSize: 18,
          letterSpacing: -0.4,
        },
        headerLeft: () => (
          <Ionicons
            name="ios-arrow-back"
            size={25}
            color="grey"
            style={{ marginLeft: 25 }}
            onPress={() => {
              navigation.navigate("PostsScreen");
            }}
          />
        ),
        tabBarIcon: ({ focused, color, size }) => (
          <Ionicons
            name={focused ? "ios-trash" : "ios-trash"}
            // size={25}
            // color={grey}
            size={size}
            color={color}
          />
        ),
      }}
    />
    <Tab.Screen
      name="Comments"
      component={CommentsScreen}
      options={{
        title: "Коментарі",
        headerStyle: {
          backgroundColor: "#fff",
        },
        headerTintColor: "#212121",
        headerTitleStyle: {
          fontWeight: "500",
          fontSize: 18,
          letterSpacing: -0.4,
        },
        headerLeft: () => (
          <Ionicons
            name="ios-arrow-back"
            size={25}
            color="grey"
            style={{ marginLeft: 25 }}
            onPress={() => {
              navigation.navigate("PostsScreen");
            }}
          />
        ),
        tabBarIcon: ({ focused, color, size }) => (
          <Ionicons
            name={focused ? "ios-trash" : "ios-trash"}
            // size={25}
            // color={grey}
            size={size}
            color={color}
            onPress={() => {
              navigation.goBack();
            }}
          />
        ),
      }}
    />
    <Tab.Screen
      name="Profile"
      component={ProfileScreen}
      options={{ headerShown: false }}
    />
  </Tab.Navigator>
);

const App = () => {
  const isLoggedIn = getVariable("entrance");
  console.log(isLoggedIn);

  return (
    <NavigationContainer>
      {isLoggedIn !== { _h: 0, _i: 0, _j: null, _k: null } ? (
        <TabNavigator />
      ) : (
        <AuthNavigator />
      )}
    </NavigationContainer>
  );
};

export default App;

